import React from "react";
import ReactDOM from 'react-dom'

function Modals (props){
     if (!props.isOpen){
         return null;
        }
   return ReactDOM.createPortal(
      <h1>MODAL</h1>,
      document.getElementById('Modals')
    )

}
export default Modals;
