import React from "react";
import confLogo from "../images/badge-header.svg";
import "./styles/BadgeToEdit.css";
import Gravatar from "react-gravatar";
import { Link } from "react-router-dom";
import api from "../api";




class BadgeToEdits extends React.Component {
  state = {
    loading: true,
    bar: false,
    error: null,
    form: {
      firstName: "",
      lastName: "",
      email: "",
      jobTitle: "",
      twitter: "",
      badgeId:"",
    },
  };

  componentDidMount() {
    this.fetchData();
    // console.log(this.props.badgeId)
  }
  fetchData = async (e) => {
    this.setState({ loading: true, error: null });
    try {
      const data = await applicationCache.badges.read(this.props.badgeId);
      console.log(data);
      this.setState({ loading: false, form: data, });
      console.log(data);
    } catch (error) {
      this.setState({ loading: false, error: error });
    }
  };
  // fetchData = async (e) => {
  //   try{
  //     const data = await api.badges.read(this.props.badgeId)
  //     console.log(data)
  //   }
  // }
  render() {

    // const badge = this.state.data;

    return (
      <div className="BadgeToEdit container-fluid">
        <div className="BadgeToEdit_header">
          <img
            className="logoconf"
            src={confLogo}
            alt="Logo de la conferencia"
          />
          <Link to={`/badges/${this.state.form.badgeId}/edit`}>
            <img
              className="lapiz"
              alt="lapiz"
              src="https://img.icons8.com/fluent/48/000000/edit.png"
            />
          </Link>
        </div>
        <div className="BadgeToEdit_section-name">
          <Gravatar
            size={800}
            default="monsterid"
            email={this.props.email}
            className="BadgeToEdit_avatar"
          />
          <h1 className="fullname">
            {this.props.firstName} <br />
            {this.props.lastName}
          </h1>
        </div>
        <div className="BadgeToEdit_section-info ">
          <h3>{this.props.jobTitle}</h3>
          <div>{this.props.email}</div>
          <br />
          <div>@{this.props.twitter}</div>
        </div>
        <div className="BadgeToEdit_footer">#platziconf</div>
      </div>
    );
  }
}

export default BadgeToEdits;
